// Acceso a la memoria global del workflow para no repetir temas
const data = $getWorkflowStaticData('global');

// Listas de keywords y estilo desde el nodo anterior
const keywords = $input.first().json.keywords;
const visualStyles = $input.first().json.visualStyles;

// Inicializar array de palabras usadas si no existe
if (!data.usedKeywords) {
  data.usedKeywords = [];
}

// -----------------------------------------------------------
// ROTACIÓN INTELIGENTE DE KEYWORDS
// -----------------------------------------------------------

// Filtramos las keywords que NO han sido usadas todavía
let available = keywords.filter(k => !data.usedKeywords.includes(k));

// Si ya hemos usado TODAS, reiniciamos la lista y el historial
if (available.length === 0) {
  data.usedKeywords = [];
  available = [...keywords];
}

// Elegir una keyword aleatoria de las disponibles
const selected = available[Math.floor(Math.random() * available.length)];

// Guardar la elegida como usada para la próxima vez
data.usedKeywords.push(selected);

// -----------------------------------------------------------
// LIMPIEZA PARA PROMPT DE IMAGEN
// -----------------------------------------------------------
// Aquí puedes añadir palabras que quieras quitar del prompt de imagen para que no confundan a la IA.
// Por ejemplo, quitar nombres de ciudades si la IA tiende a dibujar mapas o edificios específicos.
const wordsToRemove = /cornellà|barcelona/gi; 

// Generamos la keyword limpia para la imagen
const imageKeyword = selected.replace(wordsToRemove, '').trim();

// -----------------------------------------------------------
// SELECCIÓN DE ESTILO VISUAL
// -----------------------------------------------------------
const randomStyle = visualStyles[Math.floor(Math.random() * visualStyles.length)];

return [
  { 
    json: { 
      keyword: selected,
      imageKeyword: imageKeyword,
      selectedStyleName: randomStyle.name,
      selectedStyleDescription: randomStyle.description,
      selectedStyleMood: randomStyle.mood,
      usedKeywords: data.usedKeywords
    } 
  }
];

