const results = [];

for (const item of items) {
  let rawText = item.json.output; 

  if (!rawText) {
      // Devolvemos info de depuración envuelta correctamente
      results.push({ 
        json: { 
          debug_error: "Propiedad 'output' no encontrada", 
          available_keys: Object.keys(item.json) 
        } 
      });
      continue;
  }

  if (typeof rawText === 'string') {
    let jsonString = rawText.replace(/```json\s*/g, "").replace(/```\s*$/g, "");
    try {
      const parsed = JSON.parse(jsonString);
      // ÉXITO: Devolvemos el objeto parseado envuelto en 'json'
      results.push({ json: parsed });
    } catch (e) {
      results.push({ 
        json: { 
          debug_error: "Error parsing JSON string", 
          raw_content: rawText 
        } 
      });
    }
  } else if (typeof rawText === 'object') {
     results.push({ json: rawText });
  } else {
     results.push({ 
       json: { 
         debug_error: "Formato desconocido", 
         raw_content: rawText 
       } 
     });
  }
}

return results;

